// data contains: data.icon, data.color, data.name, data.params
return card({
    content: `
        ${icon({ name: data.icon, color: data.color, size: '48' })}
        ${cardAction({ data })}
    `
});

//data contains: data.value, data.icon and data.color
return card({
    content: `
    ${cardValue({ value: data.value, style: "font-size: 20px; font-weight: 400; margin-top: 10px" })}
    `
});

reactCard(`
  function Widget() {
    const aspect = useCardAspectRatio('${data.domId}')
    return (
          <View className="no-drag">
            <KeySetter
              nameKey={data.params.nameKey}
            />
          </View>
    );
  }

`, data.domId)
